mapper.select:
select * from product_detail_info
where pi_name like #{keyword}

    and si_seq =#{seller_seq}

order by pi_seq desc
limit 10 offset #{offset}

select a.*, b.rp_seq as recommend from product_detail_info a left outer join recommend_prod b
on a.pi_seq = b.rp_pi_seq
where pi_name like #{keyword}
order by pi_seq desc
limit 10 offset #{offset}

select count(*) from product_detail_info
where pi_name like #{keyword}

    and si_seq =#{seller_seq}

mapper.update:
update product_info
set
    pi_name =#{pi_name}, pi_price =#{pi_price}, pi_sub_title =#{pi_sub_title},pi_discount_rate =#{pi_discount_rate}, pi_point_rate =#{pi_point_rate},pi_stock =#{pi_stock}, pi_cate_seq =#{pi_cate_seq}, pi_seller_seq =#{pi_seller_seq},pi_delivery_seq =#{pi_delivery_seq}, pi_status =#{pi_status}, pi_mfi_seq =#{pi_mfi_seq}
where
    pi_seq =#{pi_seq}
